import axios from 'axios';
const Index = (quote) => {
	return (
		<div>
			<h1>{quote.content.rendered}</h1>
			<button onChange={location.reload()}>Another quote</button>
		</div>
	);
};

Index.getInitialProps = async () => {
	const res = await axios.get('https://quotesondesign.com/wp-json/wp/v2/posts/?orderby=rand');
	const { data } = res;
	console.log(data);
	let idx = Math.floor(Math.random() * Object.keys(data).length);
	let quote = data[idx];
	console.log(idx);
	console.log(quote);
	return quote;
};

export default Index;
